import 'package:intl/intl.dart';class User {  String userID,      loginID,      fullName,      countryCode,      phone,      emailID,      imageURL,      gender,      dateOfBirth;  User(this.fullName, this.emailID, this.countryCode, this.phone, this.userID,      this.loginID, this.imageURL, this.gender, this.dateOfBirth);  factory User.fromMap(Map<String, dynamic> json) {    return User(        json['fullName'],        json['email'],        json['countryCode'],        json['phone'].toString(),        json['userId'],        json['loginId'],        json['profileImage'],        "",        "");  }  factory User.fromJSON(Map<String, dynamic> json) {    return User(        json['fullName'],        json['email'],        json['countryCode'],        json['phone'].toString(),        "",        "",        json['profileImage'],        json['gender'] ?? "",        DateFormat("MMM dd yyyy").format(DateTime.parse(json['dateOfBirth'].toString()))    );  }}